function CartCard({ product, removeFromCart }) {
    const { title, price, image, id } = product

    function handleClick() {
        removeFromCart(id)
    }

    const cardStyle = {
        width: '15%',
        float: 'left'
    }

    return (
        <div className="row">
            <div style={cardStyle}>
            <img style={{ width: "100px" }} src={image} className="ui tiny image" />
            <h5>{title}</h5>
            <h5>${price}</h5>
            <button className="ui tiny button"onClick={handleClick}><i class="trash icon"></i></button>
            </div>
        </div>
    )
}

export default CartCard
