function Sort({ setSort }) {

    function onChange(e) {
        console.log(e.target.innerText)
    }

    return (
        <>
            <select 
                name="sort" 
                id="sort" 
                aria-label="sort" 
                onChange={onChange}
            >
                <option value="relevant">Relevant</option>
                <option value="priceHighLow">Price High to Low</option>
                <option value="priceLowHigh">Price Low to High</option>
            </select>
        </>
    )
}

export default Sort
