function CartCard({ product, removeFromCart }) {
    const { title, price, image, id } = product

    function handleClick() {
        removeFromCart(id)
    }

    const cardStyle = {
       width: '40%',
       backgroundColor: 'gray',
    }
    const imgDiv = {
        width: '20%',
        float: 'left'
    }
    const cardText = {
       float: 'left',
       width: '50%',
       backgroundColor: 'pink',
       paddingTop: '8%'
    }

    const buttonCol = {
        paddingTop: '8%'
    }

    return (
        <div className="row">
            <div style={cardStyle}>
                <div className="column" style={imgDiv}>
                    <img src={image} className="ui tiny image" />
                </div>
                <div className="column" style={cardText}>
                    <h5>{title}     |     ${price}</h5>
                </div>
                <div className="column" style={buttonCol}>
                    <button className="ui tiny button"onClick={handleClick}><i class="trash icon"></i></button>
                </div>
            </div>
        </div>
    )
}

export default CartCard
