    return (
        <div className="ui card" style={tempCardStyle}>
            <img src={image} style={imgStyle}/>
            <h3>{title}</h3>
            <h5>{seller}</h5>
            <h5>${price}</h5>
            <button>{inCart? "Remove from Cart" : "Add to Cart"}</button>
        </div>
    )
}

export default ProductCard
