import { Route, Switch } from 'react-router-dom'
import { useEffect, useState } from 'react'
import Header from './Header'
import Shop from './Shop'
import Sell from './Sell'
import About from './About'
import Cart from './Cart'

function App() {
  const [products, setProducts] = useState([])
  const [search, setSearch] = useState('')

  useEffect(() => {
    fetch(`http://localhost:3000/products`)
    .then(res => res.json())
    .then(setProducts)
  }, [])

  function addProduct(newProduct) {
    setProducts([...products, newProduct])
  }

  //console.log(products)

  function cartStatusChange(revisedProduct){
    products.map(product => (product.id !== revisedProduct.id) ? product : revisedProduct)
  }

  const displayProducts = search ? products.filter(product => product.title.toLowerCase().includes(search.toLowerCase())) : products


  return (
    <div>
     <Header />

    <Switch>
      <Route path="/sell">
        <Sell addProduct={addProduct}/>
      </Route>
      <Route path="/about">
        <About />
      </Route>
      <Route path="/cart">
        <Cart />
      </Route>
      <Route exact path="/">
        <Shop products={displayProducts} search={search} setSearch={setSearch} cartStatusChange={cartStatusChange}/>
      </Route>
    </Switch>

    </div>
  );
}

export default App;
